						getglobal(self:GetName().."Text"):SetText(AT_WAR);
						getglobal(self:GetName().."Text"):SetVertexColor(RED_FONT_COLOR.r, RED_FONT_COLOR.g, RED_FONT_COLOR.b);


						FactionToggleAtWar(GetSelectedFaction());
						if ( self:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
						end
						ReputationFrame_Update();


						GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
						GameTooltip:SetText(REPUTATION_AT_WAR_DESCRIPTION, nil, nil, nil, nil, 1);


						GameTooltip:Hide();



































						getglobal(self:GetName().."Text"):SetText(MOVE_TO_INACTIVE);


						if ( self:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							SetFactionInactive(GetSelectedFaction());
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
							SetFactionActive(GetSelectedFaction());
						end


						GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
						GameTooltip:SetText(REPUTATION_MOVE_TO_INACTIVE, nil, nil, nil, nil, 1);


						GameTooltip:Hide();



















						getglobal(self:GetName().."Text"):SetText(SHOW_FACTION_ON_MAINSCREEN);


						if ( self:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							SetWatchedFactionIndex(GetSelectedFaction());
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
							SetWatchedFactionIndex(0);
						end
						ReputationWatchBar_Update();


						GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
						GameTooltip:SetText(REPUTATION_SHOW_AS_XP, nil, nil, nil, nil, 1);


						GameTooltip:Hide();


















						FauxScrollFrame_OnVerticalScroll(self, offset, 16, FIZ_UpdateList_Update);






































































































						FIZ_Data.ShowQuests = this:GetChecked()
						if ( FIZ_Data.ShowQuests ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
						end
						FIZ_BuildUpdateList()
						FIZ_UpdateList_Update()


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();
































						FIZ_Data.ShowInstances = this:GetChecked()
						if ( FIZ_Data.ShowInstances ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
						end
						FIZ_BuildUpdateList()
						FIZ_UpdateList_Update()


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();
































						FIZ_Data.ShowMobs = this:GetChecked()
						if ( FIZ_Data.ShowMobs ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
						end
						FIZ_BuildUpdateList()
						FIZ_UpdateList_Update()


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();
































						FIZ_Data.ShowItems = this:GetChecked()
						if ( FIZ_Data.ShowItems ) then
							PlaySound("igMainMenuOptionCheckBoxOn");
						else
							PlaySound("igMainMenuOptionCheckBoxOff");
						end
						FIZ_BuildUpdateList()
						FIZ_UpdateList_Update()


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();















						FIZ_Data.ShowQuests = true
						FIZ_Data.ShowMobs = true
						FIZ_Data.ShowInstances = true
						FIZ_Data.ShowItems = true
						PlaySound("igMainMenuOptionCheckBoxOn");
						FIZ_BuildUpdateList()
						FIZ_UpdateList_Update()


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();














						FIZ_Data.ShowQuests = nil
						FIZ_Data.ShowMobs = nil
						FIZ_Data.ShowInstances = nil
						FIZ_Data.ShowItems = nil
						PlaySound("igMainMenuOptionCheckBoxOff");
						FIZ_BuildUpdateList()
						FIZ_UpdateList_Update()


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();














						PlaySound("igMainMenuOptionCheckBoxOn");
						FIZ_ShowUpdateEntries(true)


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();














						PlaySound("igMainMenuOptionCheckBoxOff");
						FIZ_ShowUpdateEntries(false)


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();















						PlaySound("igMainMenuOptionCheckBoxOff");
						FIZ_SupressNone(false)


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();














						PlaySound("igMainMenuOptionCheckBoxOff");
						FIZ_SupressNone(true)


						FIZ_ShowHelpToolTip(this:GetName())


						GameTooltip:Hide();








